;(function () {
  var $tables = $('[data-enhance-table-print]');

  var defaultOpts = {
      scrollX: true,
      autoWidth: false,
      aaSorting: [],
      searching: false,
      paging: false,
      ordering: false,
      info: false
      /*dom: 'Bfrtip',
      buttons: [
          {
              extend: 'print',
              text: '<i class="fa fa-print"></i>&nbsp;Print',
              autoPrint: true,
              className: 'btn btn-sm blue-hoki',
              footer: true,
              customize: function (win) {
                $(win.document.body).find('table').addClass('display').css('font-size', '8px');
              }
          },
          {
              extend: 'excelHtml5',
              text: '<i class="fa fa-download"></i>&nbsp;Export to Excel',
              className: 'btn btn-sm green-jungle',
              footer: true,
              customize: function(xlsx) {
                var sheet = xlsx.xl.worksheets['sheet1.xml'];
                    $('row:first c', sheet).attr( 's', '7' );
              }
          }
      ],*/
  };

  $tables.find('thead > tr > th:last-child').addClass('all');

  $.each($tables, function() {
      var $elem           = $(this);
      var dataURL         = $elem.attr('data-table-source');
      var lengthChange    = $elem.data('length-change') || true;
      var dataStart       = $elem.data('start') || false;
      var dataLength      = $elem.data('length') || false;

      var enablePaging    = $elem.data('enable-paging');
      if (enablePaging == false) {
          enablePaging = false;
      } else {
          enablePaging = true;
      }

      var currentOpts     = $.extend(true, defaultOpts, {});

      if ( !lengthChange ) {
          currentOpts = $.extend(true, currentOpts, {
              lengthChange: false
          });
      }

      // enable paging
      if ( !enablePaging ) {
          currentOpts = $.extend(true, currentOpts, {
              bPaginate: false
          });
      }

      // start-length as page position if requested
      if ( dataStart && dataLength ) {
          currentOpts = $.extend(true, currentOpts, {
              iDisplayStart: dataStart,
              iDisplayLength: dataLength
          });
      }

      if ( !dataURL ) {
          $elem.dataTable( currentOpts );
      } else {
          currentOpts = $.extend(true, currentOpts, {
              ajax: dataURL
          });

          $elem.dataTable( currentOpts );
      }
  });

})();